// Panier.js

import React, { useState, useEffect } from "react";
import axios from 'axios';
import ImageUtil from './chemin/vers/ImageUtil';

const Panier = () => {
  const [furnitureData, setFurnitureData] = useState([]);

  useEffect(() => {
    const fetchFurnitureData = async () => {
      try {
        const response = await axios.get('/meubles/read');
        console.log('Réponse de l\'API :', response.data);
        setFurnitureData(response.data);
      } catch (error) {
        console.error('Erreur lors de la récupération des données de meubles :', error);
      }
    };

    fetchFurnitureData();
  }, []);

  return (
    <div>
      <h2>Meubles dans le Panier :</h2>
      <div className="grid-container">
        {furnitureData.map((furniture) => (
          <div key={furniture.id} className="carousel-item">
            {furniture.images.map((image) => (
              <img
                key={image.id}
                src={`data:${image.type};base64, ${ImageUtil.decompressImage(image.image)}`}
                alt={`Meuble ${furniture.id}`}
              />
            ))}
            <p>{furniture.nom}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Panier;
