import React, { useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./App.css";




export default function App() {
  const [isNavOpen, setNavOpen] = useState(false);

  const toggleNav = () => {
    setNavOpen(!isNavOpen);
  };

  // Paramètres du carousel
  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000, // Définissez la vitesse de rotation en millisecondes
  };


  const carouselImages = ["table.png", "chaise.jpeg", "armoire.jpeg"];
  const gridImages = ["table.png", "chaise.jpeg", "armoire.jpeg","chaisegamer.jpeg","lampe.webp","armoireantique.jpeg"];

  return (
    <div className="App">
      <header className="App-header">

        {/* Photo de profil comme bouton pour activer/désactiver le menu */}
        <div className="profile-container" onClick={toggleNav}>
          <img
            src={process.env.PUBLIC_URL + "/user.jpeg"}
            alt="user"
            className="profile-picture"
          />
        </div>

        {/* Barre de recherche à droite de la photo de profil */}
        <div className="search-container">
          <input
            type="text"
            placeholder="Rechercher des meubles d'occasion..."
            className="search-bar"
            id="searchBar"
          />
          <button className="search-button">Rechercher</button>
             {/* Logo du site */}
             <img
        src={process.env.PUBLIC_URL + "/LogoAmpunv.jpeg"}
        alt="Logo du site"
        className="site-logo"
      />
        </div>

      </header>

      {/* Barre de navigation sous l'image de profil (affichée ou non en fonction de isNavOpen) */}
      {isNavOpen && (
        <div className="nav-bar">
          {/* Ajoutez vos liens de navigation ici */}
          <Route path="/profile" component={Profile} />

          <a href="/meubles">Meubles</a>
          <a href="/panier">Panier</a>
        </div>
      )}

      {/* Ajout d'un espace (padding) entre le carousel et la grille */}
      <div className="spacer"></div>
      <h2 style={{ textAlign: "left", marginLeft: "100px" }}>Les Bons plans :</h2>
    {/* Carousel avec 3 composants */}
<Slider {...carouselSettings} className="carousel-container">
  {carouselImages.map((image, index) => (
    <div key={index} className="carousel-item">
      <img
        src={process.env.PUBLIC_URL + `/meuble/${image}`}
        alt={`Meuble ${index + 1}`}
      />
      <p>Nom du Meuble {index + 1}</p>
    </div>
  ))}
</Slider>

<br></br>
<br></br>
<br></br>

<h3>Les Nouveautées:</h3>

{/* Grille de conteneurs pour afficher les meubles */}
<div className="grid-container">
  {gridImages.map((image, index) => (
    <div key={index} className="grid-item">
      <img
        src={process.env.PUBLIC_URL + `/meuble/${image}`}
        alt={`Meuble ${index + 1}`}
      />
      <p>Nom du Meuble {index + 1}</p>
      <p>Prix: [Ajouter le prix ici]</p>
    </div>
  ))}
</div>
    </div>
  );
}
