// Panier.js

import React, { useState, useEffect } from "react";
import axios from 'axios';
import Slider from 'react-slick';
import './Panier.css';

const Panier = () => {
  const [furnitureData, setFurnitureData] = useState([]);

  useEffect(() => {
    const fetchFurnitureData = async () => {
      try {
        const response = await axios.get('/meubles/read'); // Assurez-vous que l'URL est correcte
        console.log('Response from API:', response.data);
        setFurnitureData(response.data);
      } catch (error) {
        console.error('Error fetching furniture data:', error);
      }
    };

    fetchFurnitureData();
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  const secondarySettings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1
  };

  const [, setMainImageIndex] = useState(0);

  return (
    <div>
      <h2>Meubles dans le Panier :</h2>
      <div className="grid-container">
        {furnitureData.map((furniture, gridIndex) => (
          <div key={furniture.id} className="carousel-item">
            <Slider {...settings}>
              {furniture.images.map((image, imageIndex) => (
                <div key={image.id}>
                  <img
                    src={`http://localhost:8080/image/${image.name}`}
                    alt={`Meuble ${furniture.id}`}
                  />
                </div>
              ))}
            </Slider>
            <Slider {...secondarySettings}>
              {furniture.images.map((image, imageIndex) => (
                <div key={image.id}>
                  <img
                    src={`http://localhost:8080/image/${image.name}`}
                    alt={`Meuble ${furniture.id}`}
                    onClick={() => setMainImageIndex(imageIndex)}
                  />
                </div>
              ))}
            </Slider>
            <p>{furniture.nom}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Panier;
