import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, NavLink, Route, Routes } from "react-router-dom";
import Slider from "react-slick";
import axios from 'axios';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./App.css";
import Profile from "./Profile";
import Footer from "./footer";
import Encart from "./encart";
import Panier from "./Panier";
import Login from "./Login";
import ProductManagement from "./gestionproduit";
import NotFound from "./NotFound";
import SearchResults from "./SearchResults";

export default function App() {
  const [isNavOpen, setNavOpen] = useState(false);
  const [furnitureData, setFurnitureData] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const toggleNav = () => {
    setNavOpen(!isNavOpen);
  };

  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  useEffect(() => {
    const fetchFurnitureData = async () => {
      try {
        const response = await axios.get('/meubles/read');
        console.log('Response from API:', response.data);
        setFurnitureData(response.data);
      } catch (error) {
        console.error('Error fetching furniture data:', error);
      }
    };

    fetchFurnitureData();
  }, []);

  const handleSearch = () => {
    const results = furnitureData.filter((furniture) =>
      furniture.nom.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setSearchResults(results);
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          {/* ... Votre code de barre de navigation ... */}

          <Routes>
            <Route
              path="/profile"
              element={
                <div className="sell-button">
                  <button onClick={() => window.location.href = "/profile/sell"}>
                    Vendre un meuble
                  </button>
                </div>
              }
            />
            <Route
              path="/search-results"
              element={<SearchResults results={searchResults} />}
            />
          </Routes>
        </header>

        <Routes>
          <Route path="/profile/*" element={<Profile />} />
          <Route path="/panier" element={<Panier />} />
          <Route path="/Login/*" element ={<Login/>} />
          <Route path="/Encart" element={<Encart />} />
          <Route
            path="/"
            element={
              <>
                {/* ... Votre code pour la section "Les Bons plans" ... */}
                <Slider {...carouselSettings} className="carousel-container">
                  {furnitureData.map((furniture) => (
                    <div key={furniture.id} className="carousel-item">
                      <img src={`http://localhost:8080/image/info/${furniture.images[0].name}`} alt={`Meuble ${furniture.id}`} />
                      <p>{furniture.nom}</p>
                    </div>
                  ))}
                </Slider>
                {/* ... Votre code pour la section "Les Nouveautés" ... */}
                <div className="grid-container">
                  {searchResults.length > 0 ? (
                    searchResults.map((furniture) => (
                      <div key={furniture.id} className="grid-item">
                        <img src={`http://localhost:8080/image/info/${furniture.images[0].name}`} alt={`Meuble ${furniture.id}`} />
                        <p>{furniture.nom}</p>
                        <p>Prix: {furniture.prix} €</p>
                      </div>
                    ))
                  ) : (
                    furnitureData.map((furniture) => (
                      <div key={furniture.id} className="grid-item">
                        <img src={`http://localhost:8080/image/info/${furniture.images[0].name}`} alt={`Meuble ${furniture.id}`} />
                        <p>{furniture.nom}</p>
                        <p>Prix: {furniture.prix} €</p>
                        <a href="./encart">En savoir plus</a> 
                      </div>
                    ))
                  )}
                </div>
              </>
            }
          />
          <Route path="/product-management" element={<ProductManagement />} />
          <Route path="*" element={<NotFound />} />
        </Routes>
      </div>
      <Footer />
    </Router>
  );
}
