import React, { useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./App.css";

export default function App() {
  const [isNavOpen, setNavOpen] = useState(false);

  const toggleNav = () => {
    setNavOpen(!isNavOpen);
  };

  // Paramètres du carousel
  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000, // Définissez la vitesse de rotation en millisecondes
  };

  return (
    <div className="App">
      <header className="App-header">
        {/* Photo de profil comme bouton pour activer/désactiver le menu */}
        <div className="profile-container" onClick={toggleNav}>
        <img
  src={process.env.PUBLIC_URL + "/table.png"}/>

        </div>

        {/* Barre de recherche à droite de la photo de profil */}
        <input
          type="text"
          placeholder="Rechercher des meubles d'occasion..."
          className="search-bar"
        />
      </header>

      {/* Barre de navigation sous l'image de profil (affichée ou non en fonction de isNavOpen) */}
      {isNavOpen && (
        <div className="nav-bar">
          {/* Ajoutez vos liens de navigation ici */}
          <a href="/">Accueil</a>
          <a href="/meubles">Meubles</a>
          <a href="/panier">Panier</a>
        </div>
      )}

      {/* Carousel avec 3 composants */}
      <Slider {...carouselSettings} className="carousel-container">
  <div className="carousel-item">
  <img src={process.env.PUBLIC_URL + "/table.png"} alt="Meuble 1" />


    <p>Nom du Meuble 1</p>
  </div>
  <div className="carousel-item">
    <img src={require("./meuble/table.png").default} alt="Meuble 2" />
    <p>Nom du Meuble 2</p>
  </div>
  <div className="carousel-item">
    <img src={require("./meuble/table.png").default} alt="Meuble 3" />
    <p>Nom du Meuble 3</p>
  </div>
</Slider>

    </div>
  );
}
